import React from 'react';

interface BackgroundBlurProps {
  variant: 'teal' | 'blue';
}

export const BackgroundBlur: React.FC<BackgroundBlurProps> = ({ variant }) => {
  const baseClasses = "absolute z-0 rounded-full opacity-20 bg-opacity-50 blur-[50px]";

  if (variant === 'teal') {
    return (
      <div
        className={`${baseClasses} bg-teal-100 h-[297px] right-[calc(50%_-_78.64px)] top-[calc(50%_-_78.64px)] w-[297px]`}
      />
    );
  }

  return (
    <div
      className={`${baseClasses} bg-blue-100 bottom-[calc(50%_-_79.23px)] h-[324px] left-[calc(50%_+_81.81px)] w-[324px]`}
    />
  );
};

